js:
var sp = MY_URL.split('#');
var num = 100;
var vod_type_id = sp[1];
var page = sp[2];
var d = [];

if (MY_PAGE == '1') {
d.push({
    title: "🔎",
    col_type: "input",
    desc: "搜你想要...",
    url: "'hiker://search?s=' + input + '&rule=' + MY_RULE.title"
})
}

var jsonfilepath = 'hiker://files/cache/黄色仓库/all.json';
if(vod_type_id == -1){
  deleteFile(jsonfilepath);
  d.push({
      title: '旧特征文件已删除！',
      desc: '切换其他分类即可自动加载新特征文件！',
      url: 'hiker://empty',
      col_type: 'text_center_1'
    });
}else{
  var allstr = '';
  var alljson = {};
  if(fileExist(jsonfilepath)){
    allstr = readFile(jsonfilepath);
    eval('alljson=' + allstr);
    var i = 0;

    for (let item of alljson) {
      if(item.vod_type_id == vod_type_id){
        if(i >= (page - 1)*num){
          d.push({
            title: base64Decode(item.title),
            desc: '📆 ' + item.uptime.slice(0,10) +  '   ⏰ ' + item.time,
            img: base64Decode(item.thumd),
            url: base64Decode(item.media)
          }); 
        }
        i++;
        if(i >= page*num){
          break;
        }
      }
    }
  }else{
    d.push({
      title: '特征文件不存在！',
      desc: '请检查是否有异常',
      url: 'hiker://empty'
    });
  }
}
setResult(d);

