var jsonurl = 'https://mirror.ghproxy.com/https://raw.githubusercontent.com/fuxkjd/hsck/main/dist/all.json';
var jsonfilepath = 'hiker://files/cache/黄色仓库/all.json';
var timefilepath = 'hiker://files/cache/黄色仓库/time.txt';
var allstr = '';
var alljson = {};
if(!fileExist(jsonfilepath) || !fileExist(timefilepath) || (fileExist(timefilepath) && (((new Date()).getTime() - parseInt(readFile(timefilepath))) > 43200000))){
  showLoading('特征文件获取中，请稍等……');
  allstr = request(jsonurl);
  if(allstr && allstr.length>1000000){
    saveFile(jsonfilepath, allstr);
    saveFile(timefilepath, (new Date()).getTime().toString());
  }
  hideLoading();
}

