if (MY_URL == "web") {
    d.push({
        desc: 'float&&top',
        url: url,
        col_type: 'x5_webview_single',
        extra: {
            urlInterceptor: $.toString((MY_RULE) => {
                if (input.includes('#import')) {
                    input = input.replace("https://pastebin.com/", "https://pastebin.com/raw/").replace("#import", "");
                    log(input);
                    let url = input.split("&")[0];
                    let page = input.split("&")[1];
                    log(page);
                    let html = request(url);
                    let arr = pdfa(html, "body");
                    var text = arr.map(it => {
                        return pdfh(it, "body&&Text");
                    }).join("&&");
                    return $.toString((text, MY_RULE, page) => {
                        fba.putVar(MY_RULE.title + "clipboard", page);
                        return fy_bridge_app.importRule(text);
                    }, text, MY_RULE, page)
                }
            }, MY_RULE),
            js: $.toString((title) => {
                function imgs() {
                    var url = document.location.href;
                    let page = 1;
                    if (/\/\d+$/.test(url)) {
                        page = url.split("/").slice(-1)[0];
                    }
                    if (page > 1) {
                        fba.log(page)
                    }
                    fba.putVar(title + "clipboard", page);
                    var arr = Array.from(document.querySelectorAll("table a"));
                    var lists = arr.filter(x => !x.href.includes('/archive/javascript'));
                    lists.forEach(x => {
                        if (x.href.includes("#import")) {
                            return;
                        }
                        x.href = x.href + "#import&" + page;
                    })
                }

                imgs();
                // 记录前一个滚动位置
                let beforeScrollTop = 0;

                function handleScroll() {
                    // 距顶部
                    var scrollTop =
                        document.documentElement.scrollTop || document.body.scrollTop;
                    // 可视区高度
                    var clientHeight =
                        document.documentElement.clientHeight || document.body.clientHeight;
                    // 滚动条总高度
                    var scrollHeight =
                        document.documentElement.scrollHeight || document.body.scrollHeight;
                    // 确定滚动方向
                    if (scrollTop >= beforeScrollTop) {
                        // 滚动触底
                        if (scrollTop + clientHeight >= scrollHeight) {
                            //fba.log("滚动触底");
                            var counter = 0; // 计数器，用于记录已经执行的次数
                            var intervalId = setInterval(function() {
                                //fba.log('执行操作');
                                imgs();
                                counter++;

                                if (counter >= 15) {
                                    //fba.log('停止执行');
                                    clearInterval(intervalId);
                                }
                            }, 500);
                        }
                    }
                    beforeScrollTop = scrollTop;
                }

                // 节流
                function throttle(f, w) {
                    let t = null;
                    return function() {
                        if (t) {
                            return;
                        }
                        t = setTimeout(() => {
                            //执行操作(使用apply重新绑定this的指向)
                            f.apply(this, arguments);
                            t = null;
                        }, w);
                    };
                }
                // 监听滚动
                document.onscroll = function() {
                    throttle(handleScroll(), 10000);
                };
            }, MY_RULE.title)
        }
    });
}


function formatDate(dateString) {
    var dateRegex = /(\w{3})\s(\d{1,2})(st|nd|rd|th),\s(\d{4})/;
    var match = dateString.match(dateRegex);

    if (match) {
        var month = match[1];
        var day = match[2];
        var year = match[4];

        // 将月份转换为数字表示
        var monthMap = {
            Jan: '01',
            Feb: '02',
            Mar: '03',
            Apr: '04',
            May: '05',
            Jun: '06',
            Jul: '07',
            Aug: '08',
            Sep: '09',
            Oct: '10',
            Nov: '11',
            Dec: '12'
        };
        month = monthMap[month];

        var formattedDate = year + '-' + month + '-' + day.toString().padStart(2, '0');
        return formattedDate;
    } else {
        return '无法解析日期';
    }
}


