function lazy1(input) {
    return $("").lazyRule((input) => {
        let id = input[0]
        let mime_type = input[1]
        if (mime_type == "") {
            return $("hiker://empty?page=fypage#"+id+"#noLoading#").rule(() => {
                const d = []
                $.require("hanshu").yunpan1(d)
                setResult(d)
            })
        }
        const {
            req1,
            shanchu
        } = $.require("hanshu")
        let url1 = "https://api-pan.xunlei.com/drive/v1/files/"+id+"?_magic=2021&usage=PLAY&thumbnail_size=SIZE_LARGE&with=hdr10&with=subtitle_files&with=task&with=public_share_tag"
        let html1
        html1 = req1(url1, "undefined"); //log(html1)
        if (html1.error_description == "验证码无效") html1 = req1(url1, "undefined"); //log(html1)
        if (html1.error_description) {
            if (input[2] == "删除") shanchu(id)
            toast(html1.error_description)
            if (/(magnet:|ed2k:|bt:|ftp:|\.torrent)/.test(MY_URL)) return MY_URL.split("#")[1]
            return "hiker://empty"
        }
        if (html1.audit.title == "") {
            if (JSON.stringify(html1.links) == "{}") {
                if (/(magnet:|ed2k:|bt:|ftp:|\.torrent)/.test(MY_URL)) return MY_URL.split("#")[1]
                return "hiker://empty"
            }
            let play = html1.links["application/octet-stream"].url
            if (input[2] == "删除") shanchu(id)
            if (html1.mime_type.includes("video")) {
                let name1 = ["原始画质"]
                let play1 = [play]
                html1.medias.map(item => {
                    if (item.link != null) {
                        name1.push(item.media_name)
                        play1.push(item.link.url)
                    }
                })
                return JSON.stringify({
                    names: name1,
                    urls: play1
                })+"#isVideo=true#"
            }
            if (html1.mime_type.includes("image")) return play+"#.jpg"
            if (html1.mime_type.includes("audio")) return play+"#isMusic=true#"
            return "download://"+play
        } else {
            toast(html1.audit.title)
            shanchu(id)
            if (/(magnet:|ed2k:|bt:|ftp:|\.torrent)/.test(MY_URL)) return MY_URL.split("#")[1]
            return "hiker://empty"
        }
    }, input.split("#"))
}

