//by LoyDgIk 佬
function bytesToSize(size) {
    if (size === "0") {
        return '';
    }
    const units = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
    let i = 0;
    while (size >= 1024) {
        size /= 1024;
        i++;
    }
    size = i ? Number(size.toFixed(2)) : size;
    return `${size} ${units[i]}`;
}

