    return {
        title: source.name,
        search_url: 'hiker://empty?key=**',
        searchFind: 'js:' + $.toString((url, rule_title) => {
            let d = [],
                key = getParam('key');
            let items = $.require('hiker://page/renderData?rule=' + rule_title).renderLiveData(url);
