		textarea(index).select();//选中文本
		document.execCommand('copy');//复制
	}

//切换选择
xuan=(item,type)=>{
		type=type==0?'port':'path'
		btn=$(`.${type} button.active`)
		if(item.value==btn[0].value)return;
		omit(btn)
		item.className='active'
		geturl()
	}

//获取value
getu=(type,index)=>{
		u=String($('.'+type+' button.active')[0].value).split(',')[index].trim();

if(type=='path')u=`legado://import/${u}?src=`;
		return u
}

//获取url
geturl=()=>{
		url = String(textarea(0).value).trim()
if(/^\s*$/.test(url))return textarea(2).value='';

if(url.match(/lanzou/)){//蓝奏云url拼接
try{
		if(url.match(/#.+/)){
				pwd = url.split('#')[1]
				url = url.split('#')[0]
			}
		else{
				pwd = String(textarea(1).value).trim()
			}
	}
catch(err){pwd=''}
url = getu('port',0)+
	'?'+getu('port',1)+'='+url.replace('/tp/','/').replace(/[\?#].*/g,'')+
	'&'+getu('port',2)+'='+pwd+
	'&'+getu('port',3);
}
return textarea(2).value=url=getu('path',0)+encodeURIComponent(url)
}

//导入阅读
importu=()=>{
		geturl();
		if(!url)return;
		return $('iframe')[0].src = url;
	}

//初始化
(()=>{

//格式化解析
