function login(msg){
		if(msg==undefined)return true;
		java.toast(msg);
		java.log(msg);
}


geturls=()=>{
	urls=[]
	bdy=java.connect("http://freexcomic.com").body()

	if(bdy==""||bdy==null){
	   	msg="❗️获取链路失败"

	}else{
			  res=bdy.match(/ <p class="ta-c mb10"><a href="([^"]+)"[\s\S]*?最新網址\d+/g)
     res.forEach(x=>{
				   url=x.replace(/.*(http[^"]+)[\s\S]+/g,"$1")
				   urls.push(url)
     })
     msg="💠成功获取"+urls.length+"链路" 

		}
	 dict.msg=msg
	 dict.urls=urls
	 source.setVariable(JSON.stringify(dict))
	 login(msg)
}

getGroup=(num)=>{
	 msg="💠已选择  "+source.loginUi()[3+num].name
	 dict.groupNum=num
	 source.setVariable(JSON.stringify(dict))
	 login(msg)
}

getSelectUrl=(num)=>{
	 if(dict.groupNum==0){
	  	   login("❗️请先选择分组")
	  	   return
	 	}
	 dict.selectNum=num
	 msg="💠已选择  "+source.loginUi()[7+num].name
	 source.setVariable(JSON.stringify(dict))
  login(msg)
}

choice=(type)=>{
 if(type){
