  `;



      let time, isLong;
      function onBtnStart(evt) {
        evt.preventDefault();
        time = setTimeout(() => {
          isLong = true;
          autoBtn.style.display = "block";
        }, 600);
      };
      function onBtnEnd(evt) {
        evt.preventDefault();
        if (isLong) {
