        sort.split(',').map(key1 => {
            key1 = key1.trim();
            for (i in sort2) {
                key2 = sort2[i].trim();
                if (key1 == key2) {
                    sort1.push(key1);
                    break;
                }
            }
        })
        sort = sort1.join(',')
    }
